// TODO: Version 2
import React, { FC } from 'react';
import { FieldProps } from 'formik';

interface IEvent {
  currentTarget: {
    files: {
      0: File;
      length: number;
    };
  };
}

type TProps = FileList & null & IEvent;

const CustomFileUpload: FC<TProps> = ({ field, form }: FieldProps) => {
  const handleChange = (e: IEvent) => {
    const file = e.currentTarget.files[0];
    const reader = new FileReader();
    const imgTag = document.getElementById('userAvatar');

    if (imgTag) {
      imgTag.title = file.name;

      // reader.onload = function(event) {
      //   imgTag.src = event.target.result;
      // };
      // TODO: check here
      // const onLoad = (event: any) => {
      //   imgTag.src = event.target?.result;
      // };
      //
      // reader.onload = onLoad(e);
    }

    reader.readAsDataURL(file);
    return form.setFieldValue(field.name, file);
  };

  return (
    <div>
      <input type="file" onChange={(event: TProps) => handleChange(event)} />
      <img src="" alt="" id={'userAvatar'}/>
    </div>
  );
};

export default CustomFileUpload;
