import React, { FC, useRef } from 'react';
import axios from 'axios';
import { FieldProps } from 'formik';

import style from './CustomFileInput.module.scss';

type TProps = FieldProps & { label?: string };

const CustomFileInput: FC<TProps> = ({label, onFileSelect, onFileSelectError, onFileSelectSuccess}:any) => {
  const fileInput = useRef(null);

  const handleFileInput = (e:any) => {
    // handle validations
    const file = e.target.files[0];
    if (file.size > 1024)
      onFileSelectError({ error: 'File size cannot exceed more than 1MB' });
    else onFileSelectSuccess(file);
  };

  const submitForm = () => {
    const formData = new FormData();
    formData.append('name', name);
    formData.append('file', selectedFile);

    axios
      .post(process.env.REACT_APP_BASE_URL + '/register', formData)
      .then((res) => {
        alert('File Upload success');
      })
      .catch((err) => alert('File Upload Error'));
  };

  return (
    <div>
      {label && <label className={style.customLabel}>{label}</label>}
        <input type="file" onChange={handleFileInput} />
        <button onClick={submitForm} />
    </div>
  )
};

export default CustomFileInput;
