import React, { FC } from 'react';
import { FieldProps } from 'formik';
import style from './DatePicker.module.scss';

interface IFiles {
  files: File[];
}

type TProps = FieldProps & IFiles & { label?: string };

const CustomFileInput: FC<TProps> = ({ label, field, field: { name, value }, form: { setFieldValue }}) => {

  const onSelectFile = (files: File[]) => {
    if (!files) return;

    const transformedFiles: File[] = files.map((file: File) => {
      return { ...file };
    });

    setFiles(transformedFiles);
  };

  return (
    <div>
      {label && <label className={style.customLabel}>{label}</label>}
      <input {...field} type="file" onChange={ (e) => setFieldValue(name, e.target.files) } />
    </div>
  )
};

export default CustomFileInput;
