import { useEffect, useState } from 'react';
import { userApi } from '../api/userApi';
import useGetRoles from './useRoles';

import { IKeyObject } from '../interfaces/IUser';

type TReturn = [IKeyObject[], number, boolean];

const useUsers = (activePage: number): TReturn => {
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [users, setUsers] = useState<IKeyObject[]>([]);
  const [totalNumberOfPages, setTotalNumberOfPages] = useState<number>(0);
  const [roles] = useGetRoles();

  const getUsers = async (): Promise<void> => {
    try {
      setIsLoading(true);
      const { data } = await userApi.getUser();

      if (data.total) {
        setTotalNumberOfPages(data.total / 10);
      }

      // if (index && index >= activePage - 1 * 10 && index < activePage * 10) {
      //   setUsers = copy;
      // }

      // list.map(element, id)

      // TODO: Map this list and return only according to activePage
      //ex. activePage === 1 --> 0,9
      //ex. activePage === 2 --> 10,19
      // list.map((el, id) => {id >= activePage - 1 * 10 && id < activePage * 10 } return)
      const copy = data.users.map(({ firstName, lastName, email, role, birthDate, createdAt }) => ({
        firstName,
        lastName,
        role: roles[role].label,
        email,
        birthDate,
        createdAt
      }));
      setUsers(copy);
    } catch (e) {
      console.log(e);
    } finally {
      setIsLoading(false);
    }
  };
  useEffect(() => {
    getUsers();
  }, []);
  useEffect(() => {
    getUsers();
  }, [activePage]);

  return [users, totalNumberOfPages, isLoading];
};

export default useUsers;
