import React, { FC } from 'react';
import { FieldProps } from 'formik';

import style from './CustomFileInput.module.scss';

type TProps = FieldProps & { label?: string };

const CustomFileInput: FC<TProps> = ({ label, field, field: { name, value }, form: { setFieldValue }}) => {
  return (
    <div>
      {label && <label className={style.customLabel}>{label}</label>}
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <input {...field} type="file" />
    </div>
  )
};

export default CustomFileInput;
