import React, { FC, useState } from 'react';
import { FieldProps } from 'formik';

import style from './CustomFileInput.module.scss';

interface IFileState {
  file: null;
}

type TProps = FieldProps & IFileState & { label?: string };

const CustomFileInput: FC<TProps> = ({ label, field, field: { name, value }, form: { setFieldValue }}) => {
  return (
    <div>
      {label && <label className={style.customLabel}>{label}</label>}
      <input {...field} type="file" onChange={(file) => setFieldValue(name, file)} />
      <button type="button" onClick={(file) => setFieldValue(name, file)} />
    </div>
  )
};

export default CustomFileInput;
